INSERT INTO `wp_posts` VALUES (71, 1, '2016-07-03 18:40:00', '2016-07-03 10:40:00', '<strong><b>公司愿景</b></strong>\r\n\r\n致力于成为全国智慧城市建设的领航者；国内最优秀的智慧社区整体方案提供者和运营者\r\n\r\n<strong><b>公司使命</b></strong>\r\n\r\n让城市更安全，让生活更便捷\r\n\r\n<strong><b>公司核心价值观</b></strong>\r\n\r\n持续创新，引领创业\r\n\r\n<strong><b>企业精神</b></strong>\r\n\r\n求真务实 开拓创新\r\n\r\n<strong><b>经营宗旨</b></strong>\r\n\r\n实现公司的持续成长，给全体股东创造满意的回报，为社会发展贡献力量。\r\n\r\n<strong><b>人才理念</b></strong>\r\n\r\n以人为本，德才兼备，以德为先，人尽其才\r\n\r\n<strong><b>学习理念</b></strong>\r\n\r\n读万卷书不如行万里路，\r\n行万里路不如阅人无数，\r\n阅人无数还需名师点悟。\r\n\r\n<strong><b>营销理念</b></strong>\r\n\r\n产业竞争靠产品，产品竞争靠品质\r\n\r\n<strong><b>品牌理念</b></strong>\r\n\r\n产业竞争靠产品，产品竞争靠品质\r\n\r\n<strong><b>社会责任</b></strong>\r\n\r\n担一份责任，多一份和谐\r\n\r\n<strong><b>企业活动</b></strong>\r\n\r\n2015年3月12日员工生日聚会\r\n\r\n2015年5月16日团结杯羽毛球比赛——2015年第二季度团建活动\r\n\r\n2015年8月29日健康杯羽毛球比赛——2015年第三季度团建活动\r\n\r\n2015年11月28日于天平山红枫节——第四季度团建活动\r\n\r\n2016年2月27日于灵白线——2016年第一季度团建活动', '企业文化', '', 'inherit', 'closed', 'closed', '', '70-revision-v1', '', '', '2016-07-03 18:40:00', '2016-07-03 10:40:00', '', 70, 'http://localhost/wordpress/?p=71', 0, 'revision', '', 0);
