INSERT INTO `wp_posts` VALUES (268, 1, '2017-04-28 08:54:27', '2017-04-28 00:54:27', '<span style=\"color: #000000;\">江苏盛世华安智能科技股份有限公司是一家专业从事物联网、互联网、大数据、云计算等新一代信息技术在新型智慧城市集成应用的高新技术企业。公司自成立以来，一直致力于基于图像和视频的智能识别设备，以及物联网智能安防产品的研发与生产。2016年在安防改造升级的基础上，公司延伸开发了互联网+惠民通民生集成服务平台及智慧一卡通，面向金融、教育、警务、养老、农业、医疗、电子商务等行业。公司于2016年8月成功挂牌新三板，未来还将不断开拓各城市市场，力争两年内进入创新层，提高城市智慧化、精细化水平，致力于成为中国新型智慧城市和创新文明城市建设新亮点的领航者！</span>\r\n\r\n<span lang=\"EN-US\"><span style=\"color: #000000;\"> </span></span>\r\n\r\n&nbsp;\r\n\r\n<img class=\"aligncenter wp-image-321 size-large\" src=\"http://www.jsssha.com/wp-content/uploads/2016/07/盛世华安企业公司照片-1024x492.jpg\" alt=\"盛世华安企业公司照片\" width=\"1024\" height=\"492\" />', '公司概况', '', 'inherit', 'closed', 'closed', '', '40-autosave-v1', '', '', '2017-04-28 08:54:27', '2017-04-28 00:54:27', '', 40, 'http://www.jsssha.com/?p=268', 0, 'revision', '', 0);
